css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
#install-stats-chart:not(:hover) {height: 30px !important; width: 725px !important; border-radius: 5px}
.ad, #post-install-ad, .install-note + div, #discussions-area h2, #social {display:none !important}
.author-styles > tbody > tr:hover {background-color: #bdb !important}
#main-header, #left-sidebar > * {background-color: #aca !important}
#install-stats-chart:hover {background: white}
html {background-color: #cec !important}
.more {border-top: 1px solid #cec}
body {max-width:99% !important}
a {color: #000 !important}

.install {background: #797 !important; box-shadow: 4px 5px 1px 0px #ccc}
.install:active {position: relative; left: 2px; top: 2px; box-shadow: 2px 3px 1px 0px #aaa}
.install-info {background: #bb8 !important; box-shadow: 0 0 10px -2px #bb8}
.install ,.install-info {-moz-user-select: none; -khtml-user-select: none; user-select: none}

/* Отсюда и до конца - для PageZipper`а */
[id^="pgzp_page"] #left-sidebar ,[id^="pgzp_page"] #main-header {display:none !important}
#pgzp_menu {background: -webkit-linear-gradient(45deg, #dfd 0%, #cec 100%) !important; background: -moz-linear-gradient(45deg, #dfd 0%, #cec 100%) !important; -moz-transition:all 0.5s linear; border-radius:0 0 0 15px; left:auto !important; opacity:0.5; right:0}
#pgzp_menu:hover {opacity:1}
a.pgzp_block:nth-child(4) {display:none !important}
}
