css:
@-moz-document url-prefix("https://www.pivotaltracker.com/s/projects/") {
.tracker_markup { visibility: collapse; }
.tracker_markup * { visibility: visible; }
.tracker_markup .story_name { font-weight: bold; }

.story .preview:hover .tracker_markup .story_name { display: block; }

.tracker_markup .label { font-weight:normal; background: #063; width: 4px; border-radius: 2px; display: inline-block; overflow:hidden; white-space: nowrap; height: 12px; text-indent:-10000em; margin-right: -4px; }
.tracker_markup .label.epic { background: #452481; }

.preview .tracker_markup .story_name { margin-left: 4px; }

.story .preview:hover .tracker_markup .story_name { margin-left: 0; }
.story .preview:hover .tracker_markup .label { background:none; width:auto; border-radius: 0; display: inline; height: auto; visbility: visible; margin-right: 0; }
.story .preview:hover .tracker_markup { visibility: visible; }
}
