@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sawyouatsinai.com") {
body {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
}
#wrapper-grey {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}
#wrapper-grey .wrapper-bottom {
    width: 100% !important;
}
#wrapper-grey .wrapper-bottom .wrapper-middle {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    margin: 0 !important;
    width: 100% !important;
}
#header-grey {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    height: auto !important;
    position: relative !important;
    width: 100% !important;
}
#smalllogo {
    float: none !important;
    padding: 0 !important;
    width: auto !important;
}
#header .left-col, #header-grey .left-col {
    display: none !important;
}
#header .login-box, #header-grey .login-box {
    float: left !important;
}
#header-grey {
    margin: 0 !important;
    padding: 0 !important;
    position: relative !important;
    top: auto !important;
    width: 100% !important;
}
#content {
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}
.LeftMenuTD {

    width: 10% !important;
}
.ContentTD {

    width: 90% !important;
}

}
