@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {
    
    .photo-well-view {background-color:#929292 !important;}
    .photo-sidebar-view {background-color:#666 !important;    text-shadow: 1px 1px 1px #000 !important;}
    .photo-charms-view .exif-charm-list > ul .show-extended-exif, .comments-view .comments .comment .comment-author a {color: #FFF !important;}
    .comments-view .comments-form .comment-form-field textarea {background-color: #4F4F4F !important;}
}
