css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {

    
    .photo-title-desc-view > .title-desc-block > .meta-field.photo-desc {font-size:13px!important; line-height:17px !important;}
    .photo-title-desc-view > .title-desc-block > .meta-field.photo-desc > p {line-height:17px !important;}
    .comments-view {font-size:13px!important;}
    .photo-charm-tags-tag-view, .photo-charm-tags-autotag-view {font-size:13px!important;}
    .photo-charms-view .c-charm-list > ul > li, .photo-charms-view .charm-info-list > ul > li {font-size:13px!important;}
    .photo-charms-view .exif-charm-list > ul > li {margin-bottom:10px !important;}
    .extended-exif-item .exif-name {color:#ddd;}
    .comments-view .comments-form .comment-form-field textarea {font-size:13px !important;}
    .title-desc-block textarea {font-size:13px !important;}
}
