css:
@-moz-document url-prefix("http://www.allmystery.de/themen/uh98013"), url-prefix("https://www.allmystery.de/themen/uh98013") {
.header {
    background: none !important;
    background-image:url("http://static.allmystery.de/upics/4f2d9f_Auswahl_001.png") !important;
    background-size: 100% 100% !important;
}

.topnav a {
   background-color: rgba(64,64,64,0.7) !important;
   border-radius: 5px 5px !important;
   border: 1px solid #404040 !important;
   box-shadow: 0px 0px 20px rgba(255,255,255,0.2) !important;
}

.topnav a:hover {
   background-color: rgba(255,255,255,0.7) !important;
   color: #000000 !important;
}
    
a.attitel:hover {
   background-color: rgba(255,255,255,0.7) !important;
   color: #000000 !important;
}    

a.attitel {
   font-size: 0 !important;
}

a.attitel:after {
   content: "Spelunke" !important;
   font-size: 18px !important;
   background-color: rgba(64,64,64,0.7) !important;
   border-radius: 5px 5px !important;
   border: 1px solid #404040 !important;
   box-shadow: 0px 0px 20px rgba(255,255,255,0.2) !important;
}
}
