css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("extremetech.com") {
    
    body, .article p, .article p span, .widget_disquswidget .title h3, .tags h4, .count, .paging, #intelliTXT
    {
        color: #ddd !important;
    }
    
    .box1 .photo .description p a
    {
        color: #fff !important;
    }

    html, #breadcrumbs, .article, .twocolumns, #sidebar .widget h3, .google-afc h3, .stories .btns a span, .stories .btns a:hover span, .news
    {
        background: #222 !important;
    }
    
    .twocolumns, .article, .articles img, .AR_2 h4, .widget_disquswidget .title, .stories ul li, .stories .btns, .box1 h3, .news, .box1 .photo, ._4s7c
    {
        border: 0px solid #222 !important;
    }
    
    .title, .title > h2, .articles span a, #sidebar .widget h3, .google-afc h3, .AR_2 h4, .box1 h3 em
    {
        color: #ddd !important;
    }
    
    #breadcrumbs > li, #breadcrumbs > li > a, .visual, .paging li
    {
        background: none !important;
    }
    
    .box1 .photo .description
    {
        opacity: 0.7 !important;
    }
    
    #content
    {
        width: 977px;
    }
    
    a
    {
       color: #CD830C !important;
    }
    
    a:visited
    {
       color: #805208 !important;
    }
    
    #text-5, #text-6, #newsletter_signup-2, .extreme-share, #outbrain_widget_0, .separator, .photo > .box2,
    .social, .next > a > img, .previous > a > img, .ad, #sidebar, #googleafcwidget-5, #leaderboard
    {
        display: none !important;
    }

}
