css:
@-moz-document domain("facebook.com") {


/* Убираем рекламу справа */
.rightColumnWrapper, .cardRightCol, #pagelet_rhc_footer, #pagelet_side_ads, #pagelet_ego_pane{
  display: none !important;
}

/* Немного сдвигаем правую панельку с годами */
#rightCol{
  margin-right: -130px !important;
}

/* Раздвигаем центральную часть */
body #contentCol #contentArea{
  width: 100% !important;
}

/* Раздвигаем белую панельку вверху страницы, появляется при скроле */
.fbTimelineStickyHeader .back{
  width: 980px !important;
}

/* Раздвигаем контейнер в котором содержимое поста */
.fbTimelineUnit .timelineUnitContainer:not(.recentActivityUnit){
  width: 615px !important;
}

/* Ширина правой (основной) колонки на странице пользователя */
.fbTimelineCapsule > div:first-child,
/* Ширина колонки с постами */
.fbTimelineUnit:not([data-side="r"]),
/* Ширина поля "о чем вы думаете" */
.fbTimelineUnit .timelineUnitContainer.fbTimelineComposerUnit{
  width: 640px !important;
}

/* Ширина левой (информационной) колонки на странице пользователя */
.fbTimelineCapsule ol{
  overflow: hidden !important;
}

/* Ширина поля ввода коммента */
.UFIContainer{
  width: auto !important;
}

/* Ширина поля для лайков-репостов */
.uiCommentContainer{
  width: 639px !important;
}

/* Ширина контейнера для фотки со страницы ссылкой на которую поделились  */
/*a:not(.belowUnitContent) .photoWrap{
  margin-left: 15px !important;
}*/

/* События на странице друг за другом равнение слева */
.fbTimelineTwoColumn[data-side="r"] {
  clear: left !important;
  float: left !important;
  overflow: hidden !important;
}

/* Убираем маленькую стрелочку */
.fbTimelineTwoColumn[data-side="l"] .spinePointer,
/* Убираем верхнюю границу */
.fbTimelineCapsule .topBorder,
/* Убираем нижнюю границу */
.fbTimelineCapsule .bottomBorder {
  display: none !important;
}

/* Ширина списка друзей для приглашения */
.inlineFriendInviterList li{
  width: 605px !important;
}

#stream_pagelet{
  padding-right: 30px !important;
}

div[role="main"] .uiScrollableArea{
  width: 625px !important;
}

div[role="main"], div[role="main"] .uiScrollableAreaBody{
  width: 665px !important;
}


}
