css:
/* Ipernity dark  */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ipernity.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, td.tcat, td.thead, td.vbmenu_control, center

{
background: #333333 !important;
}

div, span, a, tbody tr td

{
background-color: #333333 !important;
}

select, input, span, code, textarea, pre, label, div, dd, td

{
color: white !important;
}


/*links*/

a

{
color: #ffec8b !important;
}

a:visited

{
color: #eeee00 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
