css:
@-moz-document domain("kvraudio.com") {
/* 
	Minor tweaks for KVRAudio 
	
	Mar 30 2014 initial version by Frantz   
*/


/* remove extraneous sections */
#superheader {display: none  !important}
#header {display: none  !important}
div.featuredsidebar  {display: none  !important}

/* remove footer ad */
iframe[src*="/46860.php"] {display: none !important}

/* hide RSS icon */
a.spit.sprss {display: none  !important}

/* change color of Latest divider bar and jump to square */
#latestbar td {background-color: #C3C6C7 !important}
.ltno {background-color: #C3C6C7 !important}


/* remove icons in Latest section */
img[src*="/i/news/"] { display: none !important}

/* brighten depressing background colors */
body {background-color: #9BA2A3 !important}
#kvrblock {background-color: #C3C6C7 !important}

/* change side bar background color */
.scxp a {background-color: #253545  !important}

}
