css:
@-moz-document domain("echojs.com") {
html {
  background: #fff;
}
body {
  margin: 20px 0;
  font-size: 14px;
}
header {
  border-bottom: 4px solid #9c1919;
}
header h1 {
  font-size: 14px;
}
nav a {
  font-size: 11px;
  font-weight: bold;
}
.container {
  width: 800px;
  margin: 0 auto;
}
#content {
  margin-bottom: 0;
}
h2 {
  font-size: 16px;
}
#sitenews {
  font-size: 12px;
  margin-left: -16px;
  margin-right: -16px;
  margin-top: -35px;
}
article {
  font-size: 12px;
  margin-bottom: 6px;
}
article.active {
  border: none;
}
article h2 {
  font-size: 12px;
}
article p {
  font-size: 10px;
  opacity: 0.5;
}
article .uparrow, article .downarrow {
  font-size: 8px;
}
article address {
  font-size: 12px;
}
footer {
  display: none;
}
}
