css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://zhihudaily.ahorn.me/")
{
    /* fonts */
    *
    {
        font-family: "Microsoft Yahei", Verdana, "Helvetica Neue", "BBAlpha Sans", "S60 Sans", Arial, sans-serif;
    }

    body
    {
        margin: 0 !important;
        padding: 0 5px !important;
    }

    /* header */
    body > #header
    {
        display: none !important;
    }

    /* content area */
    body > #content
    {
        margin-top: 10px !important;
        margin-bottom: 10px !important;
    }

    /* date tag */
    body > #content .post-date-week
    {
        line-height: 33px !important;
    }
}

@-moz-document url-prefix("http://daily.zhihu.com/story/")
{
    /* fonts */
    *
    {
        font-family: "Microsoft Yahei", Verdana, "Helvetica Neue", "BBAlpha Sans", "S60 Sans", Arial, sans-serif;
    }

    /* background color */
    html, body
    {
        background-color: #76827B !important;
        padding-top: 0 !important;
    }

    /* global header */
    body > .global-header
    {
        display: none !important;
    }

    /* download banner */
    body > .download-banner
    {
        margin-top: -28px !important;
    }

    /* content area */
    body > .main-wrap
    {
        max-width: 75% !important;
        margin: 28px auto !important;
        padding: 20px 20px 0 !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0 5px 5px #000 !important;
        background: none repeat scroll 0% 0% #F6F6F1 !important;
    }

    /* content font size */
    body > .main-wrap > .content-inner
    {
        font-size: 20px !important;
    }

    /* image source text */
    body > .main-wrap .img-source
    {
        left: 40px !important;
    }

    /* hide ads */
    body > .main-wrap .qr
    {
        display: none !important;
    }

    /* hide footer */
    body > .footer
    {
        display: none !important;
    }
}
