css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(vk.com) {
    /*Привидения*/
    .app_container#app_3253866_container {
        height: 728px !important;
    }
    .news_area {
        display: none !important;
    }

    /*Туземцы*/
    div#app_1974229_container {
        height: 898px !important;
    }
}

/*Кодекс пирата*/
@-moz-document url-prefix(http://piratesvk.x-plarium.com/?api_url=http://api.vk.com)
{
    html {
        overflow: hidden !important;
    }

    body {
        overflow: hidden !important;
        position: absolute !important;
        top: -100px !important;
        height: 868px !important;
    }
}
    
/*Туземцы*/
@-moz-document url-prefix(http://tribez-social.game-insight.com/platform/vk)
{
    #topContainer.navbar-fixed-top {
        display: none !important;
    }
    
    #footer.navbar-fixed-bottom {
        display: none !important;
    }
}

/*Метро 2033*/
@-moz-document url-prefix(http://metro.krambamboo.com/metro/vk)
{
    .roll, .news {display: none !important;}
}
