css:
/* Operate on any github page */
@-moz-document domain('github.com') {

	/* Enlarge the statistics box to show both lines */
	div.stats-switcher-viewport {
		height: 80px !important;
	}

	/* Move the rainbow bar */
	div.tooltipped {
		margin-top: -45px !important;
		margin-bottom: 50px !important;
		cursor: default !important;
	}

	/* Adjust positioning of language readout */
	ol.repository-lang-stats-numbers {
		margin-top: 5px !important;
	}
}
