css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodreads.com") {
body {
    background-color: #FFF;
    background: fixed url('http://ww1.sinaimg.cn/large/74efb999gw1ef2pm61vhlj211y0jq459.jpg') !important;
}
  
    .mainContent {
	background: rgba(205,173,91,0.50) !important;
}
    
.headerContainer, #siteheader {
    background-image:  none !important;
    background-color: rgb(205,173,91) !important;
}
    
    .updatedTimestamp, a.updatedTimestamp, .updatedTimestampLeft {
    color: #333 !important;
}
    
    a {
    color: #2B2B00 !important;
    text-decoration: none;
}
    
    .greyText {
    color: #333 !important;
}

    .darkGreyText {
    color: #222;
}
    
    .selectedShelf {
    color: #111 !important;
    font-family: arial,verdana,helvetica,sans-serif;
}
    
.listFullDetails {
    color: #333 !important;
    font-family: arial,verdana,helvetica,sans-serif;
}
    
.bookMeta, .keyValue {
    color: #222 !important;
}

.elementListBrown .reviewDate {
    color: #444 !important;
}

.lightGreyText {
    color: #444 !important;
}

#details {
    font-size: 95%;
    color: #222 !important;
}

    H1, H2, H3, H4, H5, H6 {
    margin: 4px 0px;
    color: #444 !important;
    font-weight: normal;
}
    
}
