css:
@-moz-document domain("roblox.com") {
	#navigation-container a.btn-logo,
	#navigation-container a.btn-logo:visited,
	#navigation-container a.btn-logo:hover
	{
		background-image: url(http://i.imgur.com/cuhx8h2.png) !important
	}
	.site-header .HeaderDivider
	{
		border-left: 1px solid #878988 !important
	}
	.site-header
	{
		background-image:url(http://i.imgur.com/YLpNlrc.png) !important
	}
}

@-moz-document url-prefix("http://www.roblox.com/User.aspx") {
body {
background-color:#A3DEFC !important;
background-image: url("http://i.imgur.com/SMBdZCF.png")!important;
background-attachment: fixed!important; 
background-repeat: no-repeat!important;
background-size:100% 100%!important;
    }
}

@-moz-document url-prefix("http://www.roblox.com/home") {
body {
background-color:#A3DEFC !important;
background-image: url("http://i.imgur.com/SMBdZCF.png");
background-attachment: fixed; 
background-repeat: no-repeat;
background-size:100% 100%;
    }

div#testingSitePanelWrapper, #BodyWrapper {
margin: 0 auto;
opacity:.85;
background: #A3DEFC;
background: rgba(0, 0, 0, 0);
}
}
